import React, { useRef, useState } from "react";
import {
  apiB2CReturnSuggest,
  apiB2CReturnByInvoice,
  apiB2CReturnCreate,
} from "./api";

export default function B2CReturns() {
  // ---------------- BASIC STATE ----------------
  const [invoiceNo, setInvoiceNo] = useState("");
  const [mobile, setMobile] = useState("");
  const [name, setName] = useState("");
  const [invoiceDate, setInvoiceDate] = useState("");

  const [accountId, setAccountId] = useState(null); // âœ… INTERNAL ONLY

  const [billItems, setBillItems] = useState([]);

  // ---------------- SUGGEST STATE ----------------
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggest, setShowSuggest] = useState(false);
  const debounceRef = useRef(null);

  const [showPayloadPopup, setShowPayloadPopup] = useState(false);
const [payloadPreview, setPayloadPreview] = useState(null);

  // ---------------- FETCH BILL ----------------
  const searchBills = async () => {
    if (!invoiceNo) {
      alert("Enter invoice number");
      return;
    }

    try {
      const res = await apiB2CReturnByInvoice(invoiceNo);

      // header
      setAccountId(res.header?.account_id || null); // âœ… STORED, NOT SHOWN
      setMobile(res.header?.mobile || "");
      setName(res.header?.customer_name || "");
      setInvoiceDate(
        res.header?.invoice_date
          ? res.header.invoice_date.slice(0, 10)
          : ""
      );

      // items
      setBillItems(
        (res.items || []).map((i) => ({
          sale_item_id: i.sale_item_id,
          item_name: i.item_name,
          qty: Number(i.qty),
          rate: Number(i.rate),
          gst: Number(i.gst),

          checked: false,
          returnQty: Number(i.qty),
        }))
      );
    } catch (err) {
      alert(err.message || "Bill not found");
      setBillItems([]);
    }
  };

  // ---------------- GRID LOGIC ----------------
  const toggleItem = (sale_item_id) => {
    setBillItems((items) =>
      items.map((i) =>
        i.sale_item_id === sale_item_id
          ? { ...i, checked: !i.checked }
          : i
      )
    );
  };

  const updateReturnQty = (sale_item_id, val) => {
    setBillItems((items) =>
      items.map((i) =>
        i.sale_item_id === sale_item_id
          ? {
              ...i,
              returnQty: Math.min(Number(val) || 0, i.qty),
            }
          : i
      )
    );
  };

  // ---------------- SUBMIT RETURN ----------------
 const handleReturn = async () => {
  const selectedItems = billItems.filter(
    (i) => i.checked && Number(i.returnQty) > 0
  );

  if (selectedItems.length === 0) {
    alert("Select at least one item to return");
    return;
  }

  const payload = {
    return_type: "B2C",

    original_bill: {
      invoice_no: invoiceNo,
      invoice_date: invoiceDate,
      account_id: accountId,
    },

    customer: {
      mobile,
      name,
    },

    items: selectedItems.map((i) => ({
      sale_item_id: i.sale_item_id,
      item_name: i.item_name,
      sold_qty: i.qty,
      return_qty: i.returnQty,
      rate: i.rate,
      gst: i.gst,
      amount: i.returnQty * i.rate,
    })),
  };

  // ðŸ”¥ SHOW POPUP INSTEAD OF SENDING
  setPayloadPreview(payload);
  setShowPayloadPopup(true);
};


  const handleCancel = () => {
    setInvoiceNo("");
    setMobile("");
    setName("");
    setInvoiceDate("");
    setAccountId(null);
    setBillItems([]);
  };

  // ---------------- SUGGEST LOGIC ----------------
  const fetchSuggestions = (q) => {
    if (!q || q.trim().length < 2) {
      setSuggestions([]);
      setShowSuggest(false);
      return;
    }

    clearTimeout(debounceRef.current);
    debounceRef.current = setTimeout(async () => {
      try {
        const rows = await apiB2CReturnSuggest(q, 10);
        setSuggestions(rows || []);
        setShowSuggest(true);
      } catch {
        setSuggestions([]);
        setShowSuggest(false);
      }
    }, 300);
  };

  const selectSuggestion = (row) => {
    setInvoiceNo(row.invoice_no || "");
    setName(row.customer_name || "");
    setInvoiceDate(
      row.invoice_date ? row.invoice_date.slice(0, 10) : ""
    );
    setSuggestions([]);
    setShowSuggest(false);
  };

  // ---------------- UI ----------------
  return (
    <>
      <div className="small" style={{ marginBottom: 8 }}>
        ðŸ“¦ <b>B2C Returns</b> â€“ Customer sales return (without GST invoice)
      </div>

      {/* ---------- SEARCH ROW ---------- */}
      <div
        style={{
          display: "flex",
          gap: 10,
          alignItems: "center",
          marginTop: 12,
          position: "relative",
        }}
      >
        <input
          style={{ minWidth: 180, height: 34 }}
          placeholder="Invoice Number"
          value={invoiceNo}
          onChange={(e) => {
            setInvoiceNo(e.target.value);
            fetchSuggestions(e.target.value);
          }}
        />

        <input
          style={{ minWidth: 160, height: 34 }}
          placeholder="Customer Mobile"
          value={mobile}
          readOnly
        />

        <input
          style={{ minWidth: 200, height: 34 }}
          placeholder="Customer Name"
          value={name}
          readOnly
        />

        <input
          style={{ minWidth: 140, height: 34 }}
          type="date"
          value={invoiceDate}
          readOnly
        />

        <button
          style={{
            height: 34,
            padding: "0 14px",
            background: "#2563eb",
            color: "#fff",
            border: "1px solid #2563eb",
            borderRadius: 6,
            cursor: "pointer",
          }}
          onClick={searchBills}
        >
          Search Bills
        </button>

        {/* ---------- SUGGEST DROPDOWN ---------- */}
        {showSuggest && suggestions.length > 0 && (
          <div
            style={{
              position: "absolute",
              top: 40,
              left: 0,
              background: "#fff",
              border: "1px solid #d1d5db",
              borderRadius: 6,
              zIndex: 50,
              width: 360,
              maxHeight: 240,
              overflowY: "auto",
              boxShadow: "0 4px 12px rgba(0,0,0,0.15)",
            }}
          >
            {suggestions.map((s, i) => (
              <div
                key={i}
                style={{
                  padding: "8px 10px",
                  cursor: "pointer",
                  borderBottom: "1px solid #eee",
                }}
                onMouseDown={() => selectSuggestion(s)}
              >
                <b>{s.invoice_no}</b> â€” {s.customer_name}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* ---------- ITEMS GRID ---------- */}
      {billItems.length > 0 && (
        <div style={{ marginTop: 20 }}>
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              background: "#fff",
              border: "1px solid #e5e7eb",
            }}
          >
            <thead>
              <tr style={{ background: "#f3f4f6" }}>
                <th style={th}>Select</th>
                <th style={th}>Item</th>
                <th style={th}>Sold Qty</th>
                <th style={th}>Return Qty</th>
                <th style={th}>Rate</th>
                <th style={th}>GST %</th>
                <th style={th}>Amount</th>
              </tr>
            </thead>
            <tbody>
              {billItems.map((it) => {
                const amt = (it.returnQty || 0) * (it.rate || 0);
                return (
                  <tr key={it.sale_item_id}>
                    <td style={tdCenter}>
                      <input
                        type="checkbox"
                        checked={it.checked}
                        onChange={() => toggleItem(it.sale_item_id)}
                      />
                    </td>
                    <td style={td}>{it.item_name}</td>
                    <td style={tdCenter}>{it.qty}</td>
                    <td style={tdCenter}>
                      <input
                        type="number"
                        min="0"
                        max={it.qty}
                        value={it.returnQty}
                        disabled={!it.checked}
                        onChange={(e) =>
                          updateReturnQty(it.sale_item_id, e.target.value)
                        }
                        style={{ width: 70 }}
                      />
                    </td>
                    <td style={tdRight}>{it.rate}</td>
                    <td style={tdCenter}>{it.gst}</td>
                    <td style={tdRight}>{amt.toFixed(2)}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>

          {/* ---------- ACTION BUTTONS ---------- */}
          <div
            style={{
              display: "flex",
              justifyContent: "flex-end",
              gap: 12,
              marginTop: 16,
            }}
          >
            <button
              onClick={handleCancel}
              style={{
                padding: "6px 14px",
                borderRadius: 6,
                border: "1px solid #9ca3af",
                background: "#f3f4f6",
                cursor: "pointer",
              }}
            >
              Cancel
            </button>

            <button
              onClick={handleReturn}
              style={{
                padding: "6px 14px",
                borderRadius: 6,
                border: "1px solid #2563eb",
                background: "#2563eb",
                color: "#fff",
                cursor: "pointer",
              }}
            >
              Return
            </button>
          </div>
        </div>
      )}

            {/* ---------- PAYLOAD PREVIEW POPUP ---------- */}
      {showPayloadPopup && (
        <div
          style={{
            position: "fixed",
            inset: 0,
            background: "rgba(0,0,0,0.4)",
            zIndex: 999,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <div
            style={{
              background: "#fff",
              width: "70%",
              maxHeight: "80%",
              overflow: "auto",
              borderRadius: 8,
              padding: 16,
            }}
          >
            <h3 style={{ marginBottom: 10 }}>
              ðŸ“¤ Return Payload Preview
            </h3>

            <pre
              style={{
                background: "#f3f4f6",
                padding: 12,
                borderRadius: 6,
                fontSize: 13,
                maxHeight: 400,
                overflow: "auto",
              }}
            >
              {JSON.stringify(payloadPreview, null, 2)}
            </pre>

            <div
              style={{
                display: "flex",
                justifyContent: "flex-end",
                gap: 10,
                marginTop: 12,
              }}
            >
              <button
                onClick={() => setShowPayloadPopup(false)}
              >
                Close
              </button>

              <button
                onClick={async () => {
                  await apiB2CReturnCreate(payloadPreview);
                  setShowPayloadPopup(false);
                  alert("Return sent to backend");
                }}
              >
                Confirm & Send
              </button>
            </div>
          </div>
        </div>
      )}

    </>
  );
}

/* ---------- styles ---------- */
const th = {
  border: "1px solid #e5e7eb",
  padding: 8,
  fontSize: 13,
  textAlign: "center",
};

const td = {
  border: "1px solid #e5e7eb",
  padding: 8,
  fontSize: 13,
};

const tdCenter = { ...td, textAlign: "center" };
const tdRight = { ...td, textAlign: "right" };
