// src/ItemsPanel.jsx
import React, { useEffect, useState } from "react";
import {
  apiItemsDelete,
  apiItemsGetOne,
  apiItemsSearch,
  apiItemsUpsert,
} from "./api";

/* âœ… ITEM MASTER + GST */
const empty = () => ({
  id: null,
  item_name: "",
  group_name: "",
  primary_unit: "",
  hsn_code: "",
  tax_percent: "",
  cgst_percent: "",
  sgst_percent: "",
  igst_percent: "",
});

export default function ItemsPanel() {
  const [activeTab, setActiveTab] = useState("item"); // item | purchase
  const [q, setQ] = useState("");
  const [rows, setRows] = useState([]);
  const [form, setForm] = useState(empty());
  const [loading, setLoading] = useState(false);

  // ---------------- LOAD ----------------
  const load = async (query = "") => {
    setLoading(true);
    try {
      const data = await apiItemsSearch(query);
      setRows(Array.isArray(data) ? data : []);
    } catch (e) {
      alert(e.message || String(e));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load("");
  }, []);

  const clearAll = () => {
    setQ("");
    setForm(empty());
    setActiveTab("item");
    load("");
  };

  // ---------------- EDIT ----------------
  const edit = async (id) => {
    try {
      const full = await apiItemsGetOne(id);
      setForm({
        id: full.id,
        item_name: full.item_name || "",
        group_name: full.group_name || "",
        primary_unit: full.primary_unit || "",
        hsn_code: full.hsn_code || "",
        tax_percent: full.tax_percent ?? "",
        cgst_percent: full.cgst_percent ?? "",
        sgst_percent: full.sgst_percent ?? "",
        igst_percent: full.igst_percent ?? "",
      });
      setActiveTab("item"); // ðŸ‘ˆ stay in Add Item
    } catch (e) {
      alert(e.message || String(e));
    }
  };

  // ---------------- SAVE ----------------
  const save = async () => {
    if (!form.item_name.trim()) {
      alert("Item name is required");
      return;
    }

    const payload = {
      id: form.id,
      item_name: form.item_name.trim(),
      group_name: form.group_name || null,
      primary_unit: form.primary_unit || null,
      hsn_code: form.hsn_code || null,
      tax_percent:
        form.tax_percent === "" ? null : Number(form.tax_percent),
      cgst_percent:
        form.cgst_percent === "" ? null : Number(form.cgst_percent),
      sgst_percent:
        form.sgst_percent === "" ? null : Number(form.sgst_percent),
      igst_percent:
        form.igst_percent === "" ? null : Number(form.igst_percent),
    };

    try {
      await apiItemsUpsert(payload);
      alert("Saved successfully");
      setForm(empty());
      load(q);
    } catch (e) {
      alert(e.message || String(e));
    }
  };

  const del = async (id) => {
    if (!window.confirm("Delete this item?")) return;
    try {
      await apiItemsDelete(id);
      load(q);
    } catch (e) {
      alert(e.message || String(e));
    }
  };

  return (
    <div className="card">
      <div className="small">Items</div>

      {/* ---------- TABS ---------- */}
      <div className="tabs" style={{ marginTop: 10 }}>
        <button
          className={activeTab === "item" ? "tab active" : "tab"}
          onClick={() => setActiveTab("item")}
        >
          Add Item
        </button>
        <button
          className={activeTab === "purchase" ? "tab active" : "tab"}
          onClick={() => setActiveTab("purchase")}
        >
          Add Purchase
        </button>
      </div>

      {/* ---------- ADD ITEM (MASTER + GST) ---------- */}
      {activeTab === "item" && (
        <>
          <div className="row-3">
            <div>
              <label>Item Name *</label>
              <input
                value={form.item_name}
                onChange={(e) =>
                  setForm((f) => ({ ...f, item_name: e.target.value }))
                }
              />
            </div>
            <div>
              <label>Group</label>
              <input
                value={form.group_name}
                onChange={(e) =>
                  setForm((f) => ({ ...f, group_name: e.target.value }))
                }
              />
            </div>
            <div>
              <label>Primary Unit</label>
              <input
                value={form.primary_unit}
                onChange={(e) =>
                  setForm((f) => ({ ...f, primary_unit: e.target.value }))
                }
              />
            </div>
          </div>

          <div className="row-3" style={{ marginTop: 8 }}>
            <div>
              <label>HSN Code</label>
              <input
                value={form.hsn_code}
                onChange={(e) =>
                  setForm((f) => ({ ...f, hsn_code: e.target.value }))
                }
              />
            </div>
            <div>
              <label>Tax %</label>
              <input
                value={form.tax_percent}
                onChange={(e) =>
                  setForm((f) => ({ ...f, tax_percent: e.target.value }))
                }
              />
            </div>
            <div>
              <label>IGST %</label>
              <input
                value={form.igst_percent}
                onChange={(e) =>
                  setForm((f) => ({ ...f, igst_percent: e.target.value }))
                }
              />
            </div>
          </div>

          <div className="row-3" style={{ marginTop: 8 }}>
            <div>
              <label>CGST %</label>
              <input
                value={form.cgst_percent}
                onChange={(e) =>
                  setForm((f) => ({ ...f, cgst_percent: e.target.value }))
                }
              />
            </div>
            <div>
              <label>SGST %</label>
              <input
                value={form.sgst_percent}
                onChange={(e) =>
                  setForm((f) => ({ ...f, sgst_percent: e.target.value }))
                }
              />
            </div>
            <div />
          </div>
        </>
      )}

      {/* ---------- ADD PURCHASE (EMPTY FOR NOW) ---------- */}
      {activeTab === "purchase" && (
        <div className="muted" style={{ padding: 20 }}>
          Purchase entry table will be added here later.
        </div>
      )}

      {/* ---------- ACTIONS ---------- */}
      <div className="stack" style={{ marginTop: 12 }}>
        <button className="btn primary" onClick={save}>
          {form.id ? "Update" : "Save"}
        </button>
        {form.id && (
          <button className="btn" onClick={() => setForm(empty())}>
            Cancel
          </button>
        )}
      </div>

      <hr className="sep" />

      {/* ---------- SEARCH + LIST (UNCHANGED) ---------- */}
      {/* keep your existing search & table code */}
    </div>
  );
}
