import React, { useRef, useState } from "react";
import {
  apiB2CReturnSuggest,
  apiB2CReturnByInvoice,
  apiCreateB2CReturn, // â¬…ï¸ ADD THIS API
} from "./api";

export default function B2CReturns() {
  // ---------------- BASIC STATE ----------------
  const [invoiceNo, setInvoiceNo] = useState("");
  const [mobile, setMobile] = useState("");
  const [name, setName] = useState("");
  const [invoiceDate, setInvoiceDate] = useState("");

  const [billItems, setBillItems] = useState([]);

  // ---------------- SUGGEST STATE ----------------
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggest, setShowSuggest] = useState(false);
  const debounceRef = useRef(null);

  // ---------------- FETCH BILL ----------------
  const searchBills = async () => {
    if (!invoiceNo) {
      alert("Enter invoice number");
      return;
    }

    try {
      const res = await apiB2CReturnByInvoice(invoiceNo);

      // header
      setMobile(res.header?.mobile || "");
      setName(res.header?.customer_name || "");
      setInvoiceDate(
        res.header?.invoice_date
          ? res.header.invoice_date.slice(0, 10)
          : ""
      );

      // items
      setBillItems(
        (res.items || []).map((i) => ({
          ...i,
          checked: false,
          returnQty: i.qty,
        }))
      );
    } catch (err) {
      alert(err.message || "Bill not found");
      setBillItems([]);
    }
  };

  // ---------------- GRID LOGIC ----------------
  const toggleItem = (sale_item_id) => {
    setBillItems((items) =>
      items.map((i) =>
        i.sale_item_id === sale_item_id
          ? { ...i, checked: !i.checked }
          : i
      )
    );
  };

  const updateReturnQty = (sale_item_id, val) => {
    setBillItems((items) =>
      items.map((i) =>
        i.sale_item_id === sale_item_id
          ? {
              ...i,
              returnQty: Math.min(Number(val) || 0, i.qty),
            }
          : i
      )
    );
  };

  // ---------------- RETURN LOGIC ----------------
  const handleReturn = async () => {
    const selected = billItems.filter(
      (i) => i.checked && i.returnQty > 0
    );

    if (selected.length === 0) {
      alert("Select at least one item to return");
      return;
    }

    const payload = {
      original_invoice_no: invoiceNo,
      original_invoice_date: invoiceDate,
      customer_name: name,
      mobile: mobile,
      return_date: new Date().toISOString().slice(0, 10),
      return_type: "B2C",

      items: selected.map((i) => ({
        sale_item_id: i.sale_item_id,
        item_id: i.item_id,
        item_name: i.item_name,
        sold_qty: i.qty,
        return_qty: i.returnQty,
        rate: i.rate,
        gst: i.gst,
        amount: i.returnQty * i.rate,
      })),
    };

    try {
      await apiCreateB2CReturn(payload);
      alert("Return processed successfully");
      handleCancel();
    } catch (err) {
      alert("Return failed");
      console.error(err);
    }
  };

  const handleCancel = () => {
    if (!window.confirm("Cancel return process?")) return;

    setInvoiceNo("");
    setMobile("");
    setName("");
    setInvoiceDate("");
    setBillItems([]);
    setSuggestions([]);
    setShowSuggest(false);
  };

  // ---------------- SUGGEST LOGIC ----------------
  const fetchSuggestions = (q) => {
    if (!q || q.trim().length < 2) {
      setSuggestions([]);
      setShowSuggest(false);
      return;
    }

    clearTimeout(debounceRef.current);
    debounceRef.current = setTimeout(async () => {
      try {
        const rows = await apiB2CReturnSuggest(q, 10);
        setSuggestions(rows || []);
        setShowSuggest(true);
      } catch {
        setSuggestions([]);
        setShowSuggest(false);
      }
    }, 300);
  };

  const selectSuggestion = (row) => {
    setInvoiceNo(row.invoice_no || "");
    setName(row.customer_name || "");
    setInvoiceDate(
      row.invoice_date ? row.invoice_date.slice(0, 10) : ""
    );
    setSuggestions([]);
    setShowSuggest(false);
  };

  // ---------------- UI ----------------
  return (
    <>
      <div className="small" style={{ marginBottom: 8 }}>
        ðŸ“¦ <b>B2C Returns</b> â€“ Customer sales return (without GST invoice)
      </div>

      {/* ---------- SEARCH ROW ---------- */}
      <div
        style={{
          display: "flex",
          gap: 10,
          alignItems: "center",
          marginTop: 12,
          position: "relative",
        }}
      >
        <input
          style={{ minWidth: 180, height: 34 }}
          placeholder="Invoice Number"
          value={invoiceNo}
          onChange={(e) => {
            setInvoiceNo(e.target.value);
            fetchSuggestions(e.target.value);
          }}
        />

        <input
          style={{ minWidth: 160, height: 34 }}
          placeholder="Customer Mobile"
          value={mobile}
          readOnly
        />

        <input
          style={{ minWidth: 200, height: 34 }}
          placeholder="Customer Name"
          value={name}
          readOnly
        />

        <input
          style={{ minWidth: 140, height: 34 }}
          type="date"
          value={invoiceDate}
          readOnly
        />

        <button
          style={btnPrimary}
          onClick={searchBills}
        >
          Search Bills
        </button>

        {/* ---------- SUGGEST DROPDOWN ---------- */}
        {showSuggest && suggestions.length > 0 && (
          <div style={suggestBox}>
            {suggestions.map((s, i) => (
              <div
                key={i}
                style={suggestItem}
                onMouseDown={() => selectSuggestion(s)}
              >
                <b>{s.invoice_no}</b> â€” {s.customer_name}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* ---------- ITEMS GRID ---------- */}
      {billItems.length > 0 && (
        <>
          <div style={{ marginTop: 20 }}>
            <table style={table}>
              <thead>
                <tr style={{ background: "#f3f4f6" }}>
                  <th style={th}>Select</th>
                  <th style={th}>Item</th>
                  <th style={th}>Sold Qty</th>
                  <th style={th}>Return Qty</th>
                  <th style={th}>Rate</th>
                  <th style={th}>GST %</th>
                  <th style={th}>Amount</th>
                </tr>
              </thead>
              <tbody>
                {billItems.map((it) => {
                  const amt = it.returnQty * it.rate;
                  return (
                    <tr key={it.sale_item_id}>
                      <td style={tdCenter}>
                        <input
                          type="checkbox"
                          checked={it.checked}
                          onChange={() =>
                            toggleItem(it.sale_item_id)
                          }
                        />
                      </td>
                      <td style={td}>{it.item_name}</td>
                      <td style={tdCenter}>{it.qty}</td>
                      <td style={tdCenter}>
                        <input
                          type="number"
                          min="0"
                          max={it.qty}
                          value={it.returnQty}
                          disabled={!it.checked}
                          onChange={(e) =>
                            updateReturnQty(
                              it.sale_item_id,
                              e.target.value
                            )
                          }
                          style={{ width: 70 }}
                        />
                      </td>
                      <td style={tdRight}>{it.rate}</td>
                      <td style={tdCenter}>{it.gst}</td>
                      <td style={tdRight}>
                        {amt.toFixed(2)}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>

          {/* ---------- ACTION BUTTONS ---------- */}
          <div
            style={{
              display: "flex",
              justifyContent: "flex-end",
              gap: 12,
              marginTop: 16,
            }}
          >
            <button style={btnSecondary} onClick={handleCancel}>
              Cancel
            </button>
            <button style={btnPrimary} onClick={handleReturn}>
              Return
            </button>
          </div>
        </>
      )}
    </>
  );
}

/* ---------- styles ---------- */
const th = {
  border: "1px solid #e5e7eb",
  padding: 8,
  fontSize: 13,
  textAlign: "center",
};

const td = {
  border: "1px solid #e5e7eb",
  padding: 8,
  fontSize: 13,
};

const tdCenter = { ...td, textAlign: "center" };
const tdRight = { ...td, textAlign: "right" };

const table = {
  width: "100%",
  borderCollapse: "collapse",
  background: "#fff",
  border: "1px solid #e5e7eb",
};

const btnPrimary = {
  height: 34,
  padding: "0 16px",
