import React, { useRef, useState } from "react";
import {
  apiB2CReturnSuggest,
  apiB2CReturnByInvoice,
} from "./api";

export default function B2CReturns() {
  // ---------------- BASIC STATE ----------------
  const [invoiceNo, setInvoiceNo] = useState("");
  const [mobile, setMobile] = useState("");
  const [name, setName] = useState("");
  const [invoiceDate, setInvoiceDate] = useState("");
  const [accountId, setAccountId] = useState(null);

  const [billItems, setBillItems] = useState([]);

  // preview popup
  const [invoicePreview, setInvoicePreview] = useState(null);
  const [showPopup, setShowPopup] = useState(false);

  // suggest
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggest, setShowSuggest] = useState(false);
  const debounceRef = useRef(null);

  // ---------------- FETCH BILL ----------------
  const searchBills = async () => {
    if (!invoiceNo) {
      alert("Enter invoice number");
      return;
    }

    try {
      const res = await apiB2CReturnByInvoice(invoiceNo);
      setInvoicePreview(res);      // ✅ store response
      setShowPopup(true);          // ✅ open popup
    } catch (err) {
      alert(err.message || "Bill not found");
    }
  };

  // ---------------- LOAD GRID AFTER CONFIRM ----------------
  const loadItems = () => {
    const res = invoicePreview;

    setAccountId(res.header.account_id);
    setMobile(res.header.mobile || "");
    setName(res.header.customer_name || "");
    setInvoiceDate(res.header.invoice_date.slice(0, 10));

    setBillItems(
      res.items.map((i) => ({
        sale_item_id: i.sale_item_id,
        item_name: i.item_name,
        qty: Number(i.qty),
        rate: Number(i.rate),
        gst: Number(i.gst),
        checked: false,
        returnQty: Number(i.qty),
      }))
    );

    setShowPopup(false);
    setInvoicePreview(null);
  };

  // ---------------- GRID HELPERS ----------------
  const toggleItem = (id) => {
    setBillItems((rows) =>
      rows.map((r) =>
        r.sale_item_id === id ? { ...r, checked: !r.checked } : r
      )
    );
  };

  const updateReturnQty = (id, val) => {
    setBillItems((rows) =>
      rows.map((r) =>
        r.sale_item_id === id
          ? { ...r, returnQty: Math.min(Number(val), r.qty) }
          : r
      )
    );
  };

  // ---------------- SUGGEST ----------------
  const fetchSuggestions = (q) => {
    if (!q || q.length < 2) return;

    clearTimeout(debounceRef.current);
    debounceRef.current = setTimeout(async () => {
      const rows = await apiB2CReturnSuggest(q, 10);
      setSuggestions(rows || []);
      setShowSuggest(true);
    }, 300);
  };

  // ---------------- UI ----------------
  return (
    <>
      <h3>B2C Returns</h3>

      {/* SEARCH */}
      <div style={{ display: "flex", gap: 8, position: "relative" }}>
        <input
          placeholder="Invoice No"
          value={invoiceNo}
          onChange={(e) => {
            setInvoiceNo(e.target.value);
            fetchSuggestions(e.target.value);
          }}
        />

        <input value={mobile} readOnly placeholder="Mobile" />
        <input value={name} readOnly placeholder="Customer" />
        <input type="date" value={invoiceDate} readOnly />

        <button onClick={searchBills}>Search Bills</button>

        {showSuggest && suggestions.length > 0 && (
          <div style={suggestBox}>
            {suggestions.map((s, i) => (
              <div
                key={i}
                style={suggestItem}
                onMouseDown={() => {
                  setInvoiceNo(s.invoice_no);
                  setShowSuggest(false);
                }}
              >
                <b>{s.invoice_no}</b> – {s.customer_name}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* POPUP */}
      {showPopup && invoicePreview && (
        <div style={overlay}>
          <div style={popup}>
            <h4>Invoice Preview</h4>

            <p>
              <b>Invoice:</b> {invoicePreview.header.invoice_no}<br />
              <b>Date:</b> {invoicePreview.header.invoice_date}<br />
              <b>Customer:</b> {invoicePreview.header.customer_name}<br />
              <b>Total:</b> ₹{invoicePreview.header.grand_total}
            </p>

            <table width="100%" border="1">
              <thead>
                <tr>
                  <th>Item</th>
                  <th>Qty</th>
                  <th>Rate</th>
                </tr>
              </thead>
              <tbody>
                {invoicePreview.items.map((i) => (
                  <tr key={i.sale_item_id}>
                    <td>{i.item_name}</td>
                    <td>{i.qty}</td>
                    <td>{i.rate}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            <div style={{ textAlign: "right", marginTop: 10 }}>
              <button onClick={() => setShowPopup(false)}>Cancel</button>
              <button onClick={loadItems} style={{ marginLeft: 8 }}>
                Load Items
              </button>
            </div>
          </div>
        </div>
      )}

      {/* ITEMS GRID */}
      {billItems.length > 0 && (
        <table width="100%" border="1" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Select</th>
              <th>Item</th>
              <th>Sold</th>
              <th>Return</th>
              <th>Rate</th>
              <th>GST</th>
            </tr>
          </thead>
          <tbody>
            {billItems.map((it) => (
              <tr key={it.sale_item_id}>
                <td>
                  <input
                    type="checkbox"
                    checked={it.checked}
                    onChange={() => toggleItem(it.sale_item_id)}
                  />
                </td>
                <td>{it.item_name}</td>
                <td>{it.qty}</td>
                <td>
                  <input
                    type="number"
                    value={it.returnQty}
                    disabled={!it.checked}
                    onChange={(e) =>
                      updateReturnQty(it.sale_item_id, e.target.value)
                    }
                  />
                </td>
                <td>{it.rate}</td>
                <td>{it.gst}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </>
  );
}

/* ---------- styles ---------- */
const overlay = {
  position: "fixed",
  inset: 0,
  background: "rgba(0,0,0,0.4)",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
};

const popup = {
  background: "#fff",
  padding: 16,
  borderRadius: 8,
  width: 600,
};

const suggestBox = {
  position: "absolute",
  top: 36,
  left: 0,
  background: "#fff",
  border: "1px solid #ccc",
  width: 300,
  zIndex: 20,
};

const suggestItem = {
  padding: 6,
  cursor: "pointer",
};
