import React, { useEffect, useState } from "react";
import { apiB2CReturnSuggest } from "./api";

export default function B2CReturns() {
  const [query, setQuery] = useState("");
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    let timer;
    const q = (query || "").trim();

    if (q.length < 2) {
      setRows([]);
      return;
    }

    timer = setTimeout(async () => {
      setLoading(true);
      try {
        const res = await apiB2CReturnSuggest(q, 10);
        setRows(res || []);
      } catch (e) {
        console.error("B2C return suggest failed", e);
        setRows([]);
      } finally {
        setLoading(false);
      }
    }, 300);

    return () => clearTimeout(timer);
  }, [query]);

  return (
    <div>
      <div className="form-grid-2">
        <div>
          <label>Search Bill No / Mobile</label>
          <input
            placeholder="Type bill number or mobile…"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            autoComplete="off"
          />
        </div>
      </div>

      {loading && <div className="small muted">Searching…</div>}

      {rows.length > 0 && (
        <table className="items-table" style={{ marginTop: 10 }}>
          <thead>
            <tr>
              <th>Bill No</th>
              <th>Date</th>
              <th>Customer</th>
              <th>Mobile</th>
              <th className="right">Amount</th>
              <th className="center">Action</th>
            </tr>
          </thead>
          <tbody>
            {rows.map((r, i) => (
              <tr key={i}>
                <td>{r.bill_no}</td>
                <td>{r.date}</td>
                <td>{r.party_name}</td>
                <td>{r.mobile || "-"}</td>
                <td className="right">{Number(r.grand_total || 0).toFixed(2)}</td>
                <td className="center">
                  <button className="btn warn">Return</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {!loading && query.length >= 2 && rows.length === 0 && (
        <div className="small muted" style={{ marginTop: 8 }}>
          No matching B2C sales found
        </div>
      )}
    </div>
  );
}
