import React, { useEffect, useState } from "react";
import {
  apiItemsDelete,
  apiItemsGetOne,
  apiItemsSearch,
  apiItemsUpsert,
} from "./api";

const empty = () => ({
  id: null,
  item_name: "",
  alt_name: "",
  company: "",
  group_name: "",
  hsn_code: "",
  primary_unit: "",
  tax_percent: "",
  cgst_percent: "",
  sgst_percent: "",
  igst_percent: "",
  purchase_price: "",
  sale_price: "",
  mrp: "",
  exp_date: "",
  mfg_date: "",
  completebarcode: "",
  srno: "",
  min_level: "",
  max_level: "",
  reorder_level: "",
});

export default function ItemsPanel() {
  const [activeTab, setActiveTab] = useState("item"); // item | purchase
  const [q, setQ] = useState("");
  const [rows, setRows] = useState([]);
  const [form, setForm] = useState(empty());
  const [loading, setLoading] = useState(false);

  // ---------------- LOAD ----------------
  const load = async (query = "") => {
    setLoading(true);
    try {
      const data = await apiItemsSearch(query);
      setRows(Array.isArray(data) ? data : []);
    } catch (e) {
      alert(e.message || String(e));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load("");
  }, []);

  const clearAll = () => {
    setQ("");
    setForm(empty());
    setActiveTab("item");
    load("");
  };

  // ---------------- EDIT ----------------
  const edit = async (id) => {
    try {
      const full = await apiItemsGetOne(id);
      setForm({
        ...empty(),
        ...full,
        exp_date: full.exp_date ? String(full.exp_date).slice(0, 10) : "",
        mfg_date: full.mfg_date ? String(full.mfg_date).slice(0, 10) : "",
      });
      setActiveTab("purchase"); // editing usually needs purchase data
    } catch (e) {
      alert(e.message || String(e));
    }
  };

  // ---------------- SAVE ----------------
  const save = async () => {
    const payload = {
      ...form,
      tax_percent: form.tax_percent === "" ? null : Number(form.tax_percent),
      cgst_percent: form.cgst_percent === "" ? null : Number(form.cgst_percent),
      sgst_percent: form.sgst_percent === "" ? null : Number(form.sgst_percent),
      igst_percent: form.igst_percent === "" ? null : Number(form.igst_percent),
      purchase_price:
        form.purchase_price === "" ? null : Number(form.purchase_price),
      sale_price: form.sale_price === "" ? null : Number(form.sale_price),
      mrp: form.mrp === "" ? null : Number(form.mrp),
      min_level: form.min_level === "" ? null : Number(form.min_level),
      max_level: form.max_level === "" ? null : Number(form.max_level),
      reorder_level:
        form.reorder_level === "" ? null : Number(form.reorder_level),
      exp_date: form.exp_date || null,
      mfg_date: form.mfg_date || null,
    };

    try {
      await apiItemsUpsert(payload);
      alert("Saved successfully");
      setForm(empty());
      setActiveTab("item");
      load(q);
    } catch (e) {
      alert(e.message || String(e));
    }
  };

  const del = async (id) => {
    if (!window.confirm("Delete this item?")) return;
    try {
      await apiItemsDelete(id);
      load(q);
    } catch (e) {
      alert(e.message || String(e));
    }
  };

  return (
    <div className="card">
      <div className="small">Items</div>

      {/* SEARCH */}
      <div className="row-3" style={{ marginTop: 10 }}>
        <div>
          <label>Search Items</label>
          <input
            value={q}
            onChange={(e) => setQ(e.target.value)}
            placeholder="Name / HSN / Company / Group"
          />
        </div>
        <div style={{ display: "flex", gap: 8, alignItems: "end" }}>
          <button className="btn" onClick={() => load(q)}>
            Search
          </button>
          <button className="btn" onClick={clearAll}>
            Clear
          </button>
        </div>
        <div />
      </div>

      <hr className="sep" />

      {/* ---------- TABS ---------- */}
      <div className="tabs">
        <button
          className={activeTab === "item" ? "tab active" : "tab"}
          onClick={() => setActiveTab("item")}
        >
          Add Item
        </button>
        <button
          className={activeTab === "purchase" ? "tab active" : "tab"}
          onClick={() => setActiveTab("purchase")}
        >
          Add Purchase
        </button>
      </div>

      {/* ---------- ADD ITEM ---------- */}
      {activeTab === "item" && (
        <>
          <div className="row-3">
            <div>
              <label>Item Name *</label>
              <input
                value={form.item_name}
                onChange={(e) =>
                  setForm((f) => ({ ...f, item_name: e.target.value }))
                }
              />
            </div>
            <div>
              <label>Company</label>
              <input
                value={form.company}
                onChange={(e) =>
                  setForm((f) => ({ ...f, company: e.target.value }))
                }
              />
            </div>
            <div>
              <label>Group</label>
              <input
                value={form.group_name}
                onChange={(e) =>
                  setForm((f) => ({ ...f, group_name: e.target.value }))
                }
              />
            </div>
          </div>

          <div className="row-3" style={{ marginTop: 8 }}>
            <div>
              <label>HSN Code</label>
              <input
                value={form.hsn_code}
                onChange={(e) =>
                  setForm((f) => ({ ...f, hsn_code: e.target.value }))
                }
              />
            </div>
            <div>
              <label>Primary Unit</label>
              <input
                value={form.primary_unit}
                onChange={(e) =>
                  setForm((f) => ({ ...f, primary_unit: e.target.value }))
                }
              />
            </div>
            <div>
              <label>Alt Name</label>
              <input
                value={form.alt_name}
                onChange={(e) =>
                  setForm((f) => ({ ...f, alt_name: e.target.value }))
                }
              />
            </div>
          </div>
        </>
      )}

      {/* ---------- ADD PURCHASE ---------- */}
      {activeTab === "purchase" && (
        <>
          <div className="row-3">
            <div>
              <label>Purchase Price</label>
              <input
                value={form.purchase_price ?? ""}
                onChange={(e) =>
                  setForm((f) => ({ ...f, purchase_price: e.target.value }))
                }
              />
            </div>
            <div>
              <label>Sale Price</label>
              <input
                value={form.sale_price ?? ""}
                onChange={(e) =>
                  setForm((f) => ({ ...f, sale_price: e.target.value }))
                }
              />
            </div>
            <div>
              <label>MRP</label>
              <input
                value={form.mrp ?? ""}
                onChange={(e) =>
                  setForm((f) => ({ ...f, mrp: e.target.value }))
                }
              />
            </div>
          </div>

          <div className="row-3" style={{ marginTop: 8 }}>
            <div>
              <label>Tax %</label>
              <input
                value={form.tax_percent ?? ""}
                onChange={(e) =>
                  setForm((f) => ({ ...f, tax_percent: e.target.value }))
                }
              />
            </div>
            <div>
              <label>CGST %</label>
              <input
                value={form.cgst_percent ?? ""}
                onChange={(e) =>
                  setForm((f) => ({ ...f, cgst_percent: e.target.value }))
                }
              />
            </div>
            <div>
              <label>SGST %</label>
              <input
                value={form.sgst_percent ?? ""}
                onChange={(e) =>
                  setForm((f) => ({ ...f, sgst_percent: e.target.value }))
                }
              />
            </div>
          </div>

          <div className="row-3" style={{ marginTop: 8 }}>
            <div>
              <label>Exp Date</label>
              <input
                type="date"
                value={form.exp_date}
                onChange={(e) =>
                  setForm((f) => ({ ...f, exp_date: e.target.value }))
                }
              />
            </div>
            <div>
              <label>Mfg Date</label>
              <input
                type="date"
                value={form.mfg_date}
                onChange={(e) =>
                  setForm((f) => ({ ...f, mfg_date: e.target.value }))
                }
              />
            </div>
            <div />
          </div>
        </>
      )}

      {/* ---------- ACTIONS ---------- */}
      <div className="stack" style={{ marginTop: 12 }}>
        <button className="btn primary" onClick={save}>
          {form.id ? "Update" : "Save"}
        </button>
        {form.id && (
          <button className="btn" onClick={() => setForm(empty())}>
            Cancel
          </button>
        )}
      </div>

      <hr className="sep" />

      {/* ---------- LIST ---------- */}
      <div className="muted">
        Items ({rows.length}) {loading ? "Loading..." : ""}
      </div>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Company</th>
            <th>Group</th>
            <th>HSN</th>
            <th>Unit</th>
            <th>Tax%</th>
            <th>Sale</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r) => (
            <tr key={r.id}>
              <td>{r.id}</td>
              <td>{r.item_name}</td>
              <td>{r.company}</td>
              <td>{r.group_name}</td>
              <td>{r.hsn_code}</td>
              <td>{r.primary_unit}</td>
              <td>{r.tax_percent}</td>
              <td>{r.sale_price}</td>
              <td>
                <button className="btn" onClick={() => edit(r.id)}>
                  Edit
                </button>{" "}
                <button className="btn warn" onClick={() => del(r.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
