import React, { useRef, useState } from "react";
import {
  apiPurchaseReturnSuggest,
  apiPurchaseReturnByBill,
} from "./api";

export default function PurchaseReturns() {
  // ---------- HEADER ----------
  const [billNo, setBillNo] = useState("");
  const [partyName, setPartyName] = useState("");
  const [billDate, setBillDate] = useState("");

  // ---------- DATA ----------
  const [items, setItems] = useState([]);
  const [fullBill, setFullBill] = useState(null);

  // ---------- SUGGEST ----------
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggest, setShowSuggest] = useState(false);
  const debounceRef = useRef(null);

  // ---------- FETCH SUGGEST ----------
  const fetchSuggest = (q) => {
    setBillNo(q);

    if (!q || q.length < 2) {
      setSuggestions([]);
      setShowSuggest(false);
      return;
    }

    clearTimeout(debounceRef.current);
    debounceRef.current = setTimeout(async () => {
      try {
        const res = await apiPurchaseReturnSuggest(q);
        setSuggestions(res || []);
        setShowSuggest(true);
      } catch {
        setSuggestions([]);
        setShowSuggest(false);
      }
    }, 300);
  };

  // ---------- SELECT BILL ----------
  const selectBill = async (row) => {
    setBillNo(row.bill_no);
    setPartyName(row.party_name);
    setBillDate(row.bill_date);
    setSuggestions([]);
    setShowSuggest(false);

    const res = await apiPurchaseReturnByBill(row.bill_no);
    setFullBill(res);

    setItems(
      res.items.map((i) => ({
        purchase_id: i.purchase_id,
        item_name: i.item_name,
        purchasedQty: Number(i.qty),
        remainingQty: Number(i.remaining_qty),
        rate: Number(i.purchase_price),
        gst: Number(i.gst_percent),
        checked: false,
        returnQty: Number(i.remaining_qty),
      }))
    );
  };

  // ---------- GRID ----------
  const toggleItem = (id) => {
    setItems((arr) =>
      arr.map((i) =>
        i.purchase_id === id ? { ...i, checked: !i.checked } : i
      )
    );
  };

  const updateQty = (id, val) => {
    setItems((arr) =>
      arr.map((i) =>
        i.purchase_id === id
          ? {
              ...i,
              returnQty: Math.min(Number(val) || 0, i.remainingQty),
            }
          : i
      )
    );
  };

  // ---------- UI ----------
  return (
    <>
      <div style={{ marginBottom: 8 }}>
        ðŸ“¦ <b>Purchase Returns</b>
      </div>

      {/* SEARCH */}
      <div style={{ display: "flex", gap: 10, position: "relative" }}>
        <input
          placeholder="Purchase Bill No"
          value={billNo}
          onChange={(e) => fetchSuggest(e.target.value)}
          style={{ minWidth: 200 }}
        />

        <input value={partyName} readOnly placeholder="Party Name" />
        <input type="date" value={billDate} readOnly />

        {showSuggest && suggestions.length > 0 && (
          <div style={suggestBox}>
            {suggestions.map((s, i) => (
              <div
                key={i}
                style={suggestItem}
                onMouseDown={() => selectBill(s)}
              >
                <b>{s.bill_no}</b> â€” {s.party_name}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* GRID */}
      {items.length > 0 && (
        <table width="100%" border="1" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Select</th>
              <th>Item</th>
              <th>Purchased</th>
              <th>Available</th>
              <th>Return Qty</th>
              <th>Rate</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {items.map((i) => (
              <tr key={i.purchase_id}>
                <td>
                  <input
                    type="checkbox"
                    checked={i.checked}
                    onChange={() => toggleItem(i.purchase_id)}
                  />
                </td>
                <td>{i.item_name}</td>
                <td>{i.purchasedQty}</td>
                <td>{i.remainingQty}</td>
                <td>
                  <input
                    type="number"
                    disabled={!i.checked}
                    value={i.returnQty}
                    onChange={(e) =>
                      updateQty(i.purchase_id, e.target.value)
                    }
                  />
                </td>
                <td>{i.rate}</td>
                <td>{(i.returnQty * i.rate).toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </>
  );
}

/* styles */
const suggestBox = {
  position: "absolute",
  top: 38,
  left: 0,
  background: "#fff",
  border: "1px solid #ccc",
  width: 360,
  zIndex: 50,
};

const suggestItem = {
  padding: "8px 10px",
  cursor: "pointer",
};
