import React, { useRef, useState } from "react";
import {
  apiB2CReturnSuggest,
  apiB2CReturnByInvoice,
  apiB2CReturnCreate,
} from "./api";

export default function B2CReturns() {
  // ---------------- BASIC STATE ----------------
  const [invoiceNo, setInvoiceNo] = useState("");
  const [mobile, setMobile] = useState("");
  const [name, setName] = useState("");
  const [invoiceDate, setInvoiceDate] = useState("");
  const [accountId, setAccountId] = useState(null);

  const [billItems, setBillItems] = useState([]);

  // ---------------- SUGGEST STATE ----------------
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggest, setShowSuggest] = useState(false);
  const debounceRef = useRef(null);

  // ---------------- POPUP STATE ----------------
  const [showPayloadPopup, setShowPayloadPopup] = useState(false);
  const [payloadPreview, setPayloadPreview] = useState(null);

  // ---------------- FETCH BILL ----------------
  const searchBills = async () => {
    if (!invoiceNo) return alert("Enter invoice number");

    try {
      const res = await apiB2CReturnByInvoice(invoiceNo);

      setAccountId(res.header?.account_id || null);
      setMobile(res.header?.mobile || "");
      setName(res.header?.customer_name || "");
      setInvoiceDate(
        res.header?.invoice_date
          ? res.header.invoice_date.slice(0, 10)
          : ""
      );

      setBillItems(
        (res.items || []).map((i) => ({
          sale_item_id: i.sale_item_id,
          item_name: i.item_name,
          qty: Number(i.qty),
          rate: Number(i.rate),
          gst: Number(i.gst),
          checked: false,
          returnQty: Number(i.qty),
        }))
      );
    } catch (err) {
      alert("Invoice not found");
      setBillItems([]);
    }
  };

  // ---------------- GRID LOGIC ----------------
  const toggleItem = (id) => {
    setBillItems((items) =>
      items.map((i) =>
        i.sale_item_id === id ? { ...i, checked: !i.checked } : i
      )
    );
  };

  const updateReturnQty = (id, val) => {
    setBillItems((items) =>
      items.map((i) =>
        i.sale_item_id === id
          ? { ...i, returnQty: Math.min(Number(val) || 0, i.qty) }
          : i
      )
    );
  };

  // ---------------- RETURN PREVIEW ----------------
  const handleReturn = () => {
    const selected = billItems.filter(
      (i) => i.checked && i.returnQty > 0
    );

    if (selected.length === 0)
      return alert("Select at least one item");

    const payload = {
      return_type: "B2C",
      original_bill: {
        invoice_no: invoiceNo,
        invoice_date: invoiceDate,
        account_id: accountId,
      },
      customer: { mobile, name },
      items: selected.map((i) => ({
        sale_item_id: i.sale_item_id,
        item_name: i.item_name,
        sold_qty: i.qty,
        return_qty: i.returnQty,
        rate: i.rate,
        gst: i.gst,
      })),
    };

    setPayloadPreview(payload);
    setShowPayloadPopup(true);
  };

  const handleCancel = () => {
    setInvoiceNo("");
    setMobile("");
    setName("");
    setInvoiceDate("");
    setAccountId(null);
    setBillItems([]);
  };

  // ---------------- SUGGEST ----------------
  const fetchSuggestions = (q) => {
    if (!q || q.length < 2) {
      setSuggestions([]);
      setShowSuggest(false);
      return;
    }

    clearTimeout(debounceRef.current);
    debounceRef.current = setTimeout(async () => {
      const rows = await apiB2CReturnSuggest(q, 10);
      setSuggestions(rows || []);
      setShowSuggest(true);
    }, 300);
  };

  const selectSuggestion = (row) => {
    setInvoiceNo(row.invoice_no);
    setName(row.customer_name);
    setInvoiceDate(row.invoice_date?.slice(0, 10));
    setSuggestions([]);
    setShowSuggest(false);
  };

  // ---------------- UI ----------------
  return (
    <>
      <h3>üì¶ B2C Returns</h3>

      {/* SEARCH */}
      <div style={{ display: "flex", gap: 10, position: "relative" }}>
        <input
          placeholder="Invoice No"
          value={invoiceNo}
          onChange={(e) => {
            setInvoiceNo(e.target.value);
            fetchSuggestions(e.target.value);
          }}
        />
        <input value={mobile} readOnly placeholder="Mobile" />
        <input value={name} readOnly placeholder="Customer" />
        <input type="date" value={invoiceDate} readOnly />
        <button onClick={searchBills}>Search</button>

        {showSuggest && (
          <div style={suggestBox}>
            {suggestions.map((s, i) => (
              <div key={i} onMouseDown={() => selectSuggestion(s)}>
                {s.invoice_no} ‚Äî {s.customer_name}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* PAYLOAD POPUP */}
      {showPayloadPopup && payloadPreview && (
        <div style={overlay}>
          <div style={modal}>
            <h3>üîÅ Return Preview</h3>

            <p><b>Invoice:</b> {payloadPreview.original_bill.invoice_no}</p>
            <p><b>Customer:</b> {payloadPreview.customer.name}</p>

            <table width="100%">
              <thead>
                <tr>
                  <th>Item</th>
                  <th>Sold</th>
                  <th>Return</th>
                  <th>Rate</th>
                  <th>Amount</th>
                </tr>
              </thead>
              <tbody>
                {payloadPreview.items.map((i, idx) => (
                  <tr key={idx}>
                    <td>{i.item_name}</td>
                    <td>{i.sold_qty}</td>
                    <td>{i.return_qty}</td>
                    <td>{i.rate}</td>
                    <td>{(i.return_qty * i.rate).toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            <div style={{ marginTop: 12, textAlign: "right" }}>
              <button onClick={() => setShowPayloadPopup(false)}>
                Cancel
              </button>
              <button
                onClick={async () => {
                  await apiB2CReturnCreate(payloadPreview);
                  setShowPayloadPopup(false);
                  alert("Return saved");
                  handleCancel();
                }}
              >
                Confirm Return
              </button>
            </div>
          </div>
        </div>
      )}

      {/* ITEMS GRID */}
      {billItems.length > 0 && (
        <table style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Select</th>
              <th>Item</th>
              <th>Sold</th>
              <th>Return</th>
              <th>Rate</th>
              <th>GST</th>
            </tr>
          </thead>
          <tbody>
            {billItems.map((i) => (
              <tr key={i.sale_item_id}>
                <td>
                  <input
                    type="checkbox"
                    checked={i.checked}
                    onChange={() => toggleItem(i.sale_item_id)}
                  />
                </td>
                <td>{i.item_name}</td>
                <td>{i.qty}</td>
                <td>
                  <input
                    type="number"
                    value={i.returnQty}
                    disabled={!i.checked}
                    onChange={(e) =>
                      updateReturnQty(i.sale_item_id, e.target.value)
                    }
                  />
                </td>
                <td>{i.rate}</td>
                <td>{i.gst}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {billItems.length > 0 && (
        <div style={{ marginTop: 12 }}>
          <button onClick={handleCancel}>Cancel</button>
          <button onClick={handleReturn}>Return</button>
        </div>
      )}
    </>
  );
}

