// src/ItemsPanel.jsx
import React, { useEffect, useState } from "react";
import { apiItemsDelete, apiItemsGetOne, apiItemsSearch, apiItemsUpsert } from "./api";

const empty = () => ({
  id: null,
  item_name: "",
  alt_name: "",
  company: "",
  group_name: "",
  hsn_code: "",
  primary_unit: "",
  tax_percent: "",
  cgst_percent: "",
  sgst_percent: "",
  igst_percent: "",
  purchase_price: "",
  sale_price: "",
  mrp: "",
  exp_date: "",
  mfg_date: "",
  completebarcode: "",
  srno: "",
  min_level: "",
  max_level: "",
  reorder_level: "",
});

export default function ItemsPanel() {
  const [q, setQ] = useState("");
  const [rows, setRows] = useState([]);
  const [form, setForm] = useState(empty());
  const [loading, setLoading] = useState(false);
  const [showAdv, setShowAdv] = useState(false);

  const load = async (query = "") => {
    setLoading(true);
    try {
      const data = await apiItemsSearch(query);
      setRows(Array.isArray(data) ? data : []);
    } catch (e) {
      alert(e.message || String(e));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(""); }, []);

  const clearAll = () => {
    setQ("");
    setForm(empty());
    setShowAdv(false);
    load("");
  };

  const edit = async (id) => {
    try {
      const full = await apiItemsGetOne(id); // full row
      setForm({
        ...empty(),
        ...full,
        exp_date: full.exp_date ? String(full.exp_date).slice(0, 10) : "",
        mfg_date: full.mfg_date ? String(full.mfg_date).slice(0, 10) : "",
      });
      setShowAdv(true);
    } catch (e) {
      alert(e.message || String(e));
    }
  };

  const save = async () => {
    const payload = {
      ...form,
      // convert numeric-like fields
      tax_percent: form.tax_percent === "" ? null : Number(form.tax_percent),
      cgst_percent: form.cgst_percent === "" ? null : Number(form.cgst_percent),
      sgst_percent: form.sgst_percent === "" ? null : Number(form.sgst_percent),
      igst_percent: form.igst_percent === "" ? null : Number(form.igst_percent),
      purchase_price: form.purchase_price === "" ? null : Number(form.purchase_price),
      sale_price: form.sale_price === "" ? null : Number(form.sale_price),
      mrp: form.mrp === "" ? null : Number(form.mrp),
      completebarcode: form.completebarcode === "" ? null : Number(form.completebarcode),
      min_level: form.min_level === "" ? null : Number(form.min_level),
      max_level: form.max_level === "" ? null : Number(form.max_level),
      reorder_level: form.reorder_level === "" ? null : Number(form.reorder_level),
      exp_date: form.exp_date || null,
      mfg_date: form.mfg_date || null,
    };

    try {
      await apiItemsUpsert(payload);
      alert("Saved!");
      setForm(empty());
      setShowAdv(false);
      load(q);
    } catch (e) {
      alert(e.message || String(e));
    }
  };

  const del = async (id) => {
    if (!window.confirm("Delete this item?")) return;
    try {
      await apiItemsDelete(id);
      load(q);
    } catch (e) {
      alert(e.message || String(e));
    }
  };

  return (
    <div className="card">
      <div className="small">Items</div>

      {/* Search */}
      <div className="row-3" style={{ marginTop: 10 }}>
        <div>
          <label>Search Items</label>
          <input
            value={q}
            onChange={(e) => setQ(e.target.value)}
            placeholder="Type name / HSN / company / group / barcode..."
          />
        </div>
        <div style={{ display: "flex", gap: 8, alignItems: "end" }}>
          <button className="btn" onClick={() => load(q)}>Search</button>
          <button className="btn" onClick={clearAll}>Clear</button>
        </div>
        <div />
      </div>

      <hr className="sep" />

      {/* Main Form */}
      <div className="row-3">
        <div>
          <label>Item Name *</label>
          <input value={form.item_name || ""} onChange={(e)=>setForm(f=>({ ...f, item_name: e.target.value }))} />
        </div>
        <div>
          <label>Company</label>
          <input value={form.company || ""} onChange={(e)=>setForm(f=>({ ...f, company: e.target.value }))} />
        </div>
        <div>
          <label>Group Name</label>
          <input value={form.group_name || ""} onChange={(e)=>setForm(f=>({ ...f, group_name: e.target.value }))} />
        </div>
      </div>

      <div className="row-3" style={{ marginTop: 8 }}>
        <div>
          <label>HSN Code</label>
          <input value={form.hsn_code || ""} onChange={(e)=>setForm(f=>({ ...f, hsn_code: e.target.value }))} />
        </div>
        <div>
          <label>Primary Unit</label>
          <input value={form.primary_unit || ""} onChange={(e)=>setForm(f=>({ ...f, primary_unit: e.target.value }))} placeholder="KGS / KG / NOS..." />
        </div>
        <div>
          <label>Tax % (overall)</label>
          <input value={form.tax_percent ?? ""} onChange={(e)=>setForm(f=>({ ...f, tax_percent: e.target.value }))} />
        </div>
      </div>

      <div className="row-3" style={{ marginTop: 8 }}>
        <div>
          <label>Purchase Price</label>
          <input value={form.purchase_price ?? ""} onChange={(e)=>setForm(f=>({ ...f, purchase_price: e.target.value }))} />
        </div>
        <div>
          <label>Sale Price</label>
          <input value={form.sale_price ?? ""} onChange={(e)=>setForm(f=>({ ...f, sale_price: e.target.value }))} />
        </div>
        <div>
          <label>MRP</label>
          <input value={form.mrp ?? ""} onChange={(e)=>setForm(f=>({ ...f, mrp: e.target.value }))} />
        </div>
      </div>

      <div className="stack" style={{ marginTop: 10, justifyContent: "space-between" }}>
        <button className="btn" onClick={() => setShowAdv(s => !s)}>
          {showAdv ? "Hide Advanced" : "Show Advanced"}
        </button>

        <div style={{ display: "flex", gap: 8 }}>
          <button className="btn primary" onClick={save}>{form.id ? "Save / Update" : "Save"}</button>
          {form.id && <button className="btn" onClick={() => setForm(empty())}>Cancel Edit</button>}
        </div>
      </div>

      {/* Advanced */}
      {showAdv && (
        <>
          <hr className="sep" />
          <div className="row-3">
            <div>
              <label>Alt Name</label>
              <input value={form.alt_name || ""} onChange={(e)=>setForm(f=>({ ...f, alt_name: e.target.value }))} />
            </div>
            <div>
              <label>Barcode</label>
              <input value={form.completebarcode ?? ""} onChange={(e)=>setForm(f=>({ ...f, completebarcode: e.target.value }))} />
            </div>
            <div>
              <label>SrNo</label>
              <input value={form.srno || ""} onChange={(e)=>setForm(f=>({ ...f, srno: e.target.value }))} />
            </div>
          </div>

          <div className="row-3" style={{ marginTop: 8 }}>
            <div>
              <label>CGST %</label>
              <input value={form.cgst_percent ?? ""} onChange={(e)=>setForm(f=>({ ...f, cgst_percent: e.target.value }))} />
            </div>
            <div>
              <label>SGST %</label>
              <input value={form.sgst_percent ?? ""} onChange={(e)=>setForm(f=>({ ...f, sgst_percent: e.target.value }))} />
            </div>
            <div>
              <label>IGST %</label>
              <input value={form.igst_percent ?? ""} onChange={(e)=>setForm(f=>({ ...f, igst_percent: e.target.value }))} />
            </div>
          </div>

          <div className="row-3" style={{ marginTop: 8 }}>
            <div>
              <label>Exp Date</label>
              <input type="date" value={form.exp_date || ""} onChange={(e)=>setForm(f=>({ ...f, exp_date: e.target.value }))} />
            </div>
            <div>
              <label>Mfg Date</label>
              <input type="date" value={form.mfg_date || ""} onChange={(e)=>setForm(f=>({ ...f, mfg_date: e.target.value }))} />
            </div>
            <div />
          </div>

          <div className="row-3" style={{ marginTop: 8 }}>
            <div>
              <label>Min Level</label>
              <input value={form.min_level ?? ""} onChange={(e)=>setForm(f=>({ ...f, min_level: e.target.value }))} />
            </div>
            <div>
              <label>Max Level</label>
              <input value={form.max_level ?? ""} onChange={(e)=>setForm(f=>({ ...f, max_level: e.target.value }))} />
            </div>
            <div>
              <label>Reorder Level</label>
              <input value={form.reorder_level ?? ""} onChange={(e)=>setForm(f=>({ ...f, reorder_level: e.target.value }))} />
            </div>
          </div>
        </>
      )}

      <hr className="sep" />

      {/* List */}
      <div className="muted" style={{ marginBottom: 8 }}>
        Items ({rows.length}) {loading ? " - Loading..." : ""}
      </div>

      <table>
        <thead>
          <tr>
            <th style={{ width: 70 }}>ID</th>
            <th>Name</th>
            <th>Company</th>
            <th>Group</th>
            <th>HSN</th>
            <th>Unit</th>
            <th>Tax%</th>
            <th>Sale</th>
            <th className="center" style={{ width: 180 }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r) => (
            <tr key={r.id}>
              <td>{r.id}</td>
              <td>{r.item_name}</td>
              <td>{r.company || ""}</td>
              <td>{r.group_name || ""}</td>
              <td>{r.hsn_code || ""}</td>
              <td>{r.primary_unit || ""}</td>
              <td>{r.tax_percent ?? ""}</td>
              <td>{r.sale_price ?? ""}</td>
              <td className="center">
                <button className="btn" onClick={() => edit(r.id)}>Edit</button>{" "}
                <button className="btn warn" onClick={() => del(r.id)}>Delete</button>
              </td>
            </tr>
          ))}
          {rows.length === 0 && (
            <tr><td colSpan={9} className="center muted">No items found</td></tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
