import React, { useEffect, useState } from "react";
import {
  apiItemsDelete,
  apiItemsGetOne,
  apiItemsSearch,
  apiItemsUpsert,
} from "./api";

const emptyItem = () => ({
  id: null,
  item_name: "",
  alt_name: "",
  company: "",
  group_name: "",
  hsn_code: "",
  primary_unit: "",
  tax_percent: "",
  cgst_percent: "",
  sgst_percent: "",
  igst_percent: "",
  purchase_price: "",
  sale_price: "",
  mrp: "",
  exp_date: "",
  mfg_date: "",
  completebarcode: "",
  srno: "",
  min_level: "",
  max_level: "",
  reorder_level: "",
});

const emptyPurchase = () => ({
  item_id: "",
  qty: "",
  purchase_price: "",
  gst_percent: "",
  selling_price: "",
});

export default function ItemsPanel() {
  // ---------------- STATES ----------------
  const [q, setQ] = useState("");
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  const [showAddPurchase, setShowAddPurchase] = useState(true);
  const [showItemForm, setShowItemForm] = useState(false);
  const [showAdv, setShowAdv] = useState(false);

  const [form, setForm] = useState(emptyItem());
  const [purchaseRow, setPurchaseRow] = useState(emptyPurchase());

  // ---------------- LOAD ITEMS ----------------
  const load = async (query = "") => {
    setLoading(true);
    try {
      const data = await apiItemsSearch(query);
      setRows(Array.isArray(data) ? data : []);
    } catch (e) {
      alert(e.message || String(e));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load("");
  }, []);

  // ---------------- ITEM CRUD ----------------
  const editItem = async (id) => {
    const full = await apiItemsGetOne(id);
    setForm({
      ...emptyItem(),
      ...full,
      exp_date: full.exp_date ? String(full.exp_date).slice(0, 10) : "",
      mfg_date: full.mfg_date ? String(full.mfg_date).slice(0, 10) : "",
    });
    setShowItemForm(true);
    setShowAdv(true);
  };

  const saveItem = async () => {
    const payload = {
      ...form,
      tax_percent: form.tax_percent === "" ? null : Number(form.tax_percent),
      cgst_percent: form.cgst_percent === "" ? null : Number(form.cgst_percent),
      sgst_percent: form.sgst_percent === "" ? null : Number(form.sgst_percent),
      igst_percent: form.igst_percent === "" ? null : Number(form.igst_percent),
      purchase_price:
        form.purchase_price === "" ? null : Number(form.purchase_price),
      sale_price: form.sale_price === "" ? null : Number(form.sale_price),
      mrp: form.mrp === "" ? null : Number(form.mrp),
      completebarcode:
        form.completebarcode === "" ? null : form.completebarcode,
      min_level: form.min_level === "" ? null : Number(form.min_level),
      max_level: form.max_level === "" ? null : Number(form.max_level),
      reorder_level:
        form.reorder_level === "" ? null : Number(form.reorder_level),
      exp_date: form.exp_date || null,
      mfg_date: form.mfg_date || null,
    };

    await apiItemsUpsert(payload);
    alert("Item saved");
    setForm(emptyItem());
    setShowItemForm(false);
    setShowAdv(false);
    load(q);
  };

  const delItem = async (id) => {
    if (!window.confirm("Delete this item?")) return;
    await apiItemsDelete(id);
    load(q);
  };

  // ---------------- UI ----------------
  return (
    <div className="card">
      <div className="small">Items</div>

      {/* SEARCH */}
      <div className="row-3" style={{ marginTop: 10 }}>
        <input
          placeholder="Type name / HSN / company / group / barcode..."
          value={q}
          onChange={(e) => setQ(e.target.value)}
        />
        <button className="btn" onClick={() => load(q)}>
          Search
        </button>
        <button className="btn" onClick={() => load("")}>
          Clear
        </button>
      </div>

      <hr className="sep" />

      {/* ADD PURCHASE */}
      <div className="card soft">
        <div className="stack space-between">
          <div className="small">Add Purchase</div>
          <button
            className="btn"
            onClick={() => setShowAddPurchase((s) => !s)}
          >
            {showAddPurchase ? "Hide" : "Show"}
          </button>
        </div>

        {showAddPurchase && (
          <>
            <table>
              <thead>
                <tr>
                  <th>Item</th>
                  <th>Qty</th>
                  <th>Purchase Price</th>
                  <th>GST %</th>
                  <th>Selling Price</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>
                    <select
                      value={purchaseRow.item_id}
                      onChange={(e) =>
                        setPurchaseRow((p) => ({
                          ...p,
                          item_id: e.target.value,
                        }))
                      }
                    >
                      <option value="">Select Item</option>
                      {rows.map((r) => (
                        <option key={r.id} value={r.id}>
                          {r.item_name}
                        </option>
                      ))}
                    </select>
                  </td>
                  <td>
                    <input
                      value={purchaseRow.qty}
                      onChange={(e) =>
                        setPurchaseRow((p) => ({
                          ...p,
                          qty: e.target.value,
                        }))
                      }
                    />
                  </td>
                  <td>
                    <input
                      value={purchaseRow.purchase_price}
                      onChange={(e) =>
                        setPurchaseRow((p) => ({
                          ...p,
                          purchase_price: e.target.value,
                        }))
                      }
                    />
                  </td>
                  <td>
                    <input
                      value={purchaseRow.gst_percent}
                      onChange={(e) =>
                        setPurchaseRow((p) => ({
                          ...p,
                          gst_percent: e.target.value,
                        }))
                      }
                    />
                  </td>
                  <td>
                    <input
                      value={purchaseRow.selling_price}
                      onChange={(e) =>
                        setPurchaseRow((p) => ({
                          ...p,
                          selling_price: e.target.value,
                        }))
                      }
                    />
                  </td>
                  <td>
                    <button className="btn primary">Add</button>
                  </td>
                </tr>
              </tbody>
            </table>

            <button
              className="btn"
              style={{ marginTop: 10 }}
              onClick={() => setShowItemForm(true)}
            >
              + Add New Item
            </button>
          </>
        )}
      </div>

      {/* ITEM MASTER FORM (MOVED HERE) */}
      {showItemForm && (
        <div className="card soft" style={{ marginTop: 12 }}>
          <div className="stack space-between">
            <div className="small">Item Master</div>
            <button
              className="btn"
              onClick={() => {
                setShowItemForm(false);
                setForm(emptyItem());
                setShowAdv(false);
              }}
            >
              Close
            </button>
          </div>

          {/* BASIC */}
          <div className="row-3">
            <input
              placeholder="Item Name *"
              value={form.item_name}
              onChange={(e) =>
                setForm((f) => ({ ...f, item_name: e.target.value }))
              }
            />
            <input
              placeholder="Company"
              value={form.company}
              onChange={(e) =>
                setForm((f) => ({ ...f, company: e.target.value }))
              }
            />
            <input
              placeholder="Group"
              value={form.group_name}
              onChange={(e) =>
                setForm((f) => ({ ...f, group_name: e.target.value }))
              }
            />
          </div>

          <div className="row-3">
            <input
              placeholder="HSN"
              value={form.hsn_code}
              onChange={(e) =>
                setForm((f) => ({ ...f, hsn_code: e.target.value }))
              }
            />
            <input
              placeholder="Unit"
              value={form.primary_unit}
              onChange={(e) =>
                setForm((f) => ({ ...f, primary_unit: e.target.value }))
              }
            />
            <input
              placeholder="Tax %"
              value={form.tax_percent}
              onChange={(e) =>
                setForm((f) => ({ ...f, tax_percent: e.target.value }))
              }
            />
          </div>

          <button className="btn primary" onClick={saveItem}>
            Save Item
          </button>
        </div>
      )}

      <hr className="sep" />

      {/* ITEMS LIST */}
      <div className="muted">
        Items ({rows.length}) {loading ? "Loading..." : ""}
      </div>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Company</th>
            <th>Group</th>
            <th>HSN</th>
            <th>Unit</th>
            <th>Tax%</th>
            <th>Sale</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r) => (
            <tr key={r.id}>
              <td>{r.id}</td>
              <td>{r.item_name}</td>
              <td>{r.company}</td>
              <td>{r.group_name}</td>
              <td>{r.hsn_code}</td>
              <td>{r.primary_unit}</td>
              <td>{r.tax_percent}</td>
              <td>{r.sale_price}</td>
              <td>
                <button className="btn" onClick={() => editItem(r.id)}>
                  Edit
                </button>
                <button className="btn warn" onClick={() => delItem(r.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
