// src/ItemsPanel.jsx
import React, { useEffect, useState } from "react";
import {
  apiItemsDelete,
  apiItemsGetOne,
  apiItemsSearch,
  apiItemsUpsert,
  apiPurchasesSearch,
} from "./api";

/* ---------------- ITEM MASTER ---------------- */
const emptyItem = () => ({
  id: null,
  item_name: "",
  group_name: "",
  primary_unit: "",
  hsn_code: "",
  tax_percent: "",
  cgst_percent: "",
  sgst_percent: "",
  igst_percent: "",
});

/* ---------------- PURCHASE ---------------- */
const emptyPurchase = () => ({
  item_name: "",
  qty: "",
  purchase_price: "",
  selling_price: "",
  mrp: "",
  gst_percent: "",
  bill_no: "",
  party_name: "",
  date: "",
});

export default function ItemsPanel() {
  const [activeTab, setActiveTab] = useState("item");
  const [q, setQ] = useState("");
  const [purchaseQ, setPurchaseQ] = useState("");

  const [rows, setRows] = useState([]);
  const [purchaseRows, setPurchaseRows] = useState([]);

  const [form, setForm] = useState(emptyItem());
  const [purchaseForm, setPurchaseForm] = useState(emptyPurchase());

  const [loading, setLoading] = useState(false);

  // ---------- PURCHASE POPUP ----------
  const [showPurchasePopup, setShowPurchasePopup] = useState(false);
  const [selectedPurchase, setSelectedPurchase] = useState(null);

  /* ---------------- LOAD ITEMS ---------------- */
  const loadItems = async (query = "") => {
    setLoading(true);
    try {
      const data = await apiItemsSearch(query);
      setRows(Array.isArray(data) ? data : []);
    } finally {
      setLoading(false);
    }
  };

  /* ---------------- LOAD PURCHASES ---------------- */
  const loadPurchases = async (query = "") => {
    setLoading(true);
    try {
      const data = await apiPurchasesSearch(query);
      setPurchaseRows(Array.isArray(data) ? data : []);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadItems("");
  }, []);

  useEffect(() => {
    if (activeTab === "purchase") loadPurchases("");
  }, [activeTab]);

  /* ---------------- EDIT ITEM ---------------- */
  const editItem = async (id) => {
    const full = await apiItemsGetOne(id);
    setForm({
      id: full.id,
      item_name: full.item_name || "",
      group_name: full.group_name || "",
      primary_unit: full.primary_unit || "",
      hsn_code: full.hsn_code || "",
      tax_percent: full.tax_percent ?? "",
      cgst_percent: full.cgst_percent ?? "",
      sgst_percent: full.sgst_percent ?? "",
      igst_percent: full.igst_percent ?? "",
    });
    setActiveTab("item");
  };

  /* ---------------- SAVE ITEM ---------------- */
  const saveItem = async () => {
    if (!form.item_name.trim()) {
      alert("Item name is required");
      return;
    }

    await apiItemsUpsert({
      id: form.id,
      item_name: form.item_name.trim(),
      group_name: form.group_name || null,
      primary_unit: form.primary_unit || null,
      hsn_code: form.hsn_code || null,
      tax_percent: form.tax_percent === "" ? null : Number(form.tax_percent),
      cgst_percent: form.cgst_percent === "" ? null : Number(form.cgst_percent),
      sgst_percent: form.sgst_percent === "" ? null : Number(form.sgst_percent),
      igst_percent: form.igst_percent === "" ? null : Number(form.igst_percent),
    });

    alert("Item saved");
    setForm(emptyItem());
    loadItems(q);
  };

  /* ---------------- DELETE ITEM ---------------- */
  const delItem = async (id) => {
    if (!window.confirm("Delete this item?")) return;
    await apiItemsDelete(id);
    loadItems(q);
  };

  /* ---------------- SAVE PURCHASE (UI ONLY) ---------------- */
  const savePurchase = () => {
    if (!purchaseForm.item_name || !purchaseForm.qty) {
      alert("Item name and quantity are required");
      return;
    }

    console.log("PURCHASE PAYLOAD:", purchaseForm);

    alert("Purchase captured (backend integration next)");
    setPurchaseForm(emptyPurchase());
    loadPurchases(purchaseQ);
  };

  /* ---------------- OPEN PURCHASE POPUP ---------------- */
  const openPurchasePopup = (p) => {
    setSelectedPurchase({
      item_name: p.item_name || "",
      qty: p.qty || "",
      purchase_price: p.purchase_price || "",
      selling_price: p.selling_price || "",
      mrp: p.mrp || "",
      gst_percent: p.gst_percent || "",
      bill_no: p.bill_no || "",
      party_name: p.party_name || "",
      date: p.date ? p.date.slice(0, 10) : "",
    });
    setShowPurchasePopup(true);
  };

  return (
    <div className="card">
      <div className="small">Items</div>

      {/* ---------- TABS ---------- */}
      <div className="tabs" style={{ marginTop: 10 }}>
        <button
          className={activeTab === "item" ? "tab active" : "tab"}
          onClick={() => setActiveTab("item")}
        >
          Add Item
        </button>
        <button
          className={activeTab === "purchase" ? "tab active" : "tab"}
          onClick={() => setActiveTab("purchase")}
        >
          Add Purchase
        </button>
      </div>

      {/* ================= ADD ITEM ================= */}
      {activeTab === "item" && (
        <>
          {/* ---------- ITEM SEARCH ---------- */}
          <div className="row-3" style={{ marginBottom: 10 }}>
            <div>
              <label>Search Items</label>
              <input
                value={q}
                onChange={(e) => setQ(e.target.value)}
                placeholder="Item / HSN / Group"
              />
            </div>

            <div style={{ display: "flex", gap: 8, alignItems: "end" }}>
              <button className="btn" onClick={() => loadItems(q)}>
                Search
              </button>
              <button
                className="btn"
                onClick={() => {
                  setQ("");
                  loadItems("");
                }}
              >
                Clear
              </button>
            </div>
            <div />
          </div>

          {/* ---------- ADD / EDIT ITEM FORM ---------- */}
          <div className="row-3">
            <div>
              <label>Item Name *</label>
              <input
                value={form.item_name}
                onChange={(e) =>
                  setForm((f) => ({ ...f, item_name: e.target.value }))
                }
              />
            </div>

            <div>
              <label>Group</label>
              <input
                value={form.group_name}
                onChange={(e) =>
                  setForm((f) => ({ ...f, group_name: e.target.value }))
                }
              />
            </div>

            <div>
              <label>Primary Unit</label>
              <input
                value={form.primary_unit}
                onChange={(e) =>
                  setForm((f) => ({ ...f, primary_unit: e.target.value }))
                }
              />
            </div>
          </div>

          <div className="stack" style={{ marginTop: 12 }}>
            <button className="btn primary" onClick={saveItem}>
              {form.id ? "Update" : "Save"}
            </button>
          </div>

          <hr className="sep" />

          {/* ---------- ITEM LIST ---------- */}
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Group</th>
                <th>HSN</th>
                <th>Unit</th>
                <th>Tax%</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {rows.map((r) => (
                <tr key={r.id}>
                  <td>{r.id}</td>
                  <td>{r.item_name}</td>
                  <td>{r.group_name}</td>
                  <td>{r.hsn_code}</td>
                  <td>{r.primary_unit}</td>
                  <td>{r.tax_percent}</td>
                  <td>
                    <button className="btn" onClick={() => editItem(r.id)}>
                      Edit
                    </button>{" "}
                    <button className="btn warn" onClick={() => delItem(r.id)}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {/* ================= ADD PURCHASE ================= */}
      {activeTab === "purchase" && (
        <>
          {/* ---------- PURCHASE LIST ---------- */}
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Item</th>
                <th>Qty</th>
                <th>Purchase ₹</th>
                <th>Selling ₹</th>
                <th>GST %</th>
                <th>Bill No</th>
                <th>Party</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {purchaseRows.map((p) => (
                <tr
                  key={p.id}
                  onClick={() => openPurchasePopup(p)}
                  style={{ cursor: "pointer" }}
                >
                  <td>{p.id}</td>
                  <td>{p.item_name}</td>
                  <td>{p.qty}</td>
                  <td>{p.purchase_price}</td>
                  <td>{p.selling_price}</td>
                  <td>{p.gst_percent}</td>
                  <td>{p.bill_no}</td>
                  <td>{p.party_name}</td>
                  <td>{p.date?.slice(0, 10)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {/* ================= PURCHASE POPUP ================= */}
      {showPurchasePopup && selectedPurchase && (
        <div className="modal-overlay">
          <div className="modal-box">
            <h3>Purchase Details</h3>

            <div className="row-3">
              <input value={selectedPurchase.item_name} readOnly />
              <input value={selectedPurchase.qty} readOnly />
              <input value={selectedPurchase.purchase_price} readOnly />
            </div>

            <div className="row-3" style={{ marginTop: 8 }}>
              <input value={selectedPurchase.selling_price} readOnly />
              <input value={selectedPurchase.mrp} readOnly />
              <input value={selectedPurchase.gst_percent} readOnly />
            </div>

            <div className="row-3" style={{ marginTop: 8 }}>
              <input value={selectedPurchase.bill_no} readOnly />
              <input value={selectedPurchase.party_name} readOnly />
              <input value={selectedPurchase.date} readOnly />
            </div>

            <div className="stack" style={{ marginTop: 16 }}>
              <button
                className="btn"
                onClick={() => setShowPurchasePopup(false)}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
