import React, { useRef, useState } from "react";
import { apiB2CReturnSuggest } from "./api";
import { apiB2CReturnByInvoice } from "./api";

export default function B2CReturns() {
  // ---------------- BASIC STATE ----------------
  const [invoiceNo, setInvoiceNo] = useState("");
  const [mobile, setMobile] = useState("");
  const [name, setName] = useState("");
  const [invoiceDate, setInvoiceDate] = useState("");

  const [showBillsPopup, setShowBillsPopup] = useState(false);
  const [billItems, setBillItems] = useState([]);

  // ---------------- SUGGEST STATE ----------------
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggest, setShowSuggest] = useState(false);
  const debounceRef = useRef(null);



  // ---------------- RETURN LOGIC ----------------


const searchBills = async () => {
  if (!invoiceNo) {
    alert("Enter invoice number");
    return;
  }

  try {
    const res = await apiB2CReturnByInvoice(invoiceNo);

    // header
    setMobile(res.header?.mobile || "");
    setName(res.header?.customer_name || "");
    setInvoiceDate(
      res.header?.invoice_date
        ? res.header.invoice_date.slice(0, 10)
        : ""
    );

    // items grid
    setBillItems(
      (res.items || []).map((i) => ({
        ...i,
        checked: false,
        returnQty: i.qty,
      }))
    );

    setShowBillsPopup(true); // optional, if you want popup logic
  } catch (err) {
    alert(err.message || "Bill not found");
  }
};


  const toggleItem = (id) => {
    setBillItems((items) =>
      items.map((i) =>
        i.id === id ? { ...i, checked: !i.checked } : i
      )
    );
  };

  const updateReturnQty = (id, val) => {
    setBillItems((items) =>
      items.map((i) =>
        i.id === id ? { ...i, returnQty: Number(val) } : i
      )
    );
  };

  // ---------------- SUGGEST LOGIC ----------------
  const fetchSuggestions = (q) => {
    if (!q || q.trim().length < 2) {
      setSuggestions([]);
      setShowSuggest(false);
      return;
    }

    clearTimeout(debounceRef.current);
    debounceRef.current = setTimeout(async () => {
      try {
        const rows = await apiB2CReturnSuggest(q, 10);
        setSuggestions(rows || []);
        setShowSuggest(true);
      } catch (err) {
        console.error("Suggest error:", err);
        setSuggestions([]);
        setShowSuggest(false);
      }
    }, 300);
  };

  const selectSuggestion = (row) => {
    setInvoiceNo(row.invoice_no || "");
    setMobile(row.mobile || "");
    setName(row.customer_name || "");
    setInvoiceDate(
      row.invoice_date ? row.invoice_date.slice(0, 10) : ""
    );
    setSuggestions([]);
    setShowSuggest(false);
  };

  // ---------------- UI ----------------
  return (
    <>
      <div className="small" style={{ marginBottom: 8 }}>
        ðŸ“¦ <b>B2C Returns</b> â€“ Customer sales return (without GST invoice)
      </div>

  <div
  style={{
    display: "flex",
    gap: "10px",
    alignItems: "center",
    marginTop: "12px",
    flexWrap: "nowrap",   
    position: "relative",
  }}
>

        <input
          style={{ minWidth: 180, height: 34 }}
          placeholder="Invoice Number"
          value={invoiceNo}
          onChange={(e) => {
            setInvoiceNo(e.target.value);
            fetchSuggestions(e.target.value);
          }}
       
        />

        <input
          style={{ minWidth: 160, height: 34 }}
          placeholder="Customer Mobile"
          value={mobile}
          onChange={(e) => {
            setMobile(e.target.value);
            fetchSuggestions(e.target.value);
          }}
        />

        <input
          style={{ minWidth: 160, height: 34 }}
          placeholder="Customer Name"
          value={name}
          onChange={(e) => {
            setName(e.target.value);
            fetchSuggestions(e.target.value);
          }}
        />

        <input
          style={{ minWidth: 140, height: 34 }}
          type="date"
          value={invoiceDate}
          onChange={(e) => setInvoiceDate(e.target.value)}
        />

        <button
          style={{
            height: 34,
            padding: "0 14px",
            background: "#2563eb",
            color: "#fff",
            border: "1px solid #2563eb",
            borderRadius: 6,
            cursor: "pointer",
          }}
          onClick={searchBills}
        >
          Search Bills
        </button>

        {/* -------- SUGGEST DROPDOWN -------- */}
        {showSuggest && suggestions.length > 0 && (
          <div
            style={{
              position: "absolute",
              top: 40,
              left: 0,
              background: "#fff",
              border: "1px solid #d1d5db",
              borderRadius: 6,
              zIndex: 50,
              width: 360,
              maxHeight: 240,
              overflowY: "auto",
              boxShadow: "0 4px 12px rgba(0,0,0,0.15)",
            }}
          >
            {suggestions.map((s, i) => (
              <div
                key={i}
                style={{
                  padding: "8px 10px",
                  cursor: "pointer",
                  borderBottom: "1px solid #eee",
                }}
                onMouseDown={() => selectSuggestion(s)}
              >
                <b>{s.invoice_no}</b> â€” {s.customer_name} â€” {s.mobile}
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
}
